import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup, 
    Label,
    Input
} from 'reactstrap';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addItem } from '../actions/itemActions';
// import ImageUploader from 'react-images-upload';
const fs = require('fs');

class ItemModal extends Component {
		constructor(props) {
			super(props);
			this.state = {
				modal: false,
				name: '',
				pictures: [], 
				selectedFile: null
			}
			this.onDrop = this.onDrop.bind(this);
		}

		static propTypes = {
			isAuthenticated: PropTypes.bool
		}

    toggle = () => {
			this.setState({
					modal: !this.state.modal
			});
		}

		onDrop = (picture) => {
			var reader  = new FileReader();

			reader.onloadend = function () {
				console.log(reader.result);
			}
			console.log(picture.type)
			// console.log(reader.readAsDataURL(picture));
			// console.log(reader)
			// console.log("pic", picture)
			this.setState({
				pictures: this.state.pictures.concat(Buffer.from(picture))
			});
		}

		toBase64 = file => new Promise((resolve, reject) => {
			const reader = new FileReader();
			reader.readAsDataURL(file);
			reader.onload = () => resolve(reader.result);
			reader.onerror = error => reject(error);
		});

		fileChangedHandler = event => {
			this.setState({ selectedFile: this.toBase64(event.target.files[0]) })
		}

		uploadHandler = () => {
			console.log(this.state.selectedFile)
			var data = btoa(this.state.selectedFile);
			console.log('data is', data)
		}

    onChange = (e) => {
      this.setState({[e.target.name]: e.target.value})
    }

    onSubmit = (e) => {
			e.preventDefault();
			const newItem = {
					name: this.state.name,
					img: this.state.pictures[0]
			}

			// add item via add item action
			this.props.addItem(newItem);

			// close modal
			this.toggle();
		}

    render(){
			return(
				<div>
					{ this.props.isAuthenticated ? 					
						<Button color="dark" style={{marginBottom: '2rem'}} onClick={this.toggle}> 
							Share your cut!
						</Button> :
						<h4 className="mb-3 ml-4">
							Feed
						</h4>
					}

					<Modal isOpen={this.state.modal} toggle={this.toggle} >
						<ModalHeader toggle={this.toggle}>Upload Post</ModalHeader>
						<ModalBody>
						<Input type="file" onChange={this.fileChangedHandler} />
						<Button onClick={this.uploadHandler}>Upload!</Button>
						{this.state.selectedFile ? <img src={`data:image/png;base64,${this.state.selectedFile}`}/>: ''}
							{/* <ImageUploader
									withIcon={true}
									buttonText='Choose images'
									onChange={this.onDrop}
									imgExtension={['.jpg', '.gif', '.png', '.gif']}
									maxFileSize={5242880}
							/> */}
							<Form onSubmit={this.onSubmit}>
								<FormGroup>
									<Label for="item"> 
										Description
									</Label>
									<Input type="text" name="name" id="item" placeholder="Clipper length, color, style, etc." onChange={this.onChange}/>
									<Button color="dark" style={{marginTop: '2rem'}} block >
										Post
									</Button>
								</FormGroup>
							</Form>
						</ModalBody>
					</Modal>
				</div>
			);
    }
}
const mapStateToProps = state => ({
		item: state.item,
		isAuthenticated: state.auth.isAuthenticated
})

export default connect(mapStateToProps, { addItem })(ItemModal)
